package sesi_02;


public class OperatorLogic {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		boolean x = true;
		boolean y = false;

		System.out.println("x && y = " + (x && y));
		System.out.println("x || y = " + (x || y));
		System.out.println("!(x && y)= " + !(x && y));
	}

}
