package sesi_02;


public class OperatorPembanding {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 88;
		int y = 90;

		System.out.println("x == y = " + (x == y));
		System.out.println("x != y = " + (x != y));
		System.out.println("x > y = " + (x > y));
		System.out.println("x < y = " + (x < y));
		System.out.println("x >= y = " + (x >= y));
		System.out.println("x <= y = " + (x <= y));

		int a = 2;
		int b = 2;

		System.out.println("====================");

		boolean c = a == b;
		boolean d = a != b;
		boolean e = a > b;
		boolean f = a < b;
		boolean g = a >= b;
		boolean h = a <= b;

		int nilaiA = 12;
		int nilaiB = 4;

		System.out.println(nilaiA > nilaiB);
		System.out.println(nilaiA < nilaiB);
		System.out.println(nilaiA >= nilaiB);
		System.out.println(nilaiA <= nilaiB);
		System.out.println(nilaiA == nilaiB);
		System.out.println(nilaiA != nilaiB);

	}

}
